                            priceJ,
                            variant,
                            productId,
                            productPrices,
                            priceElement,
                            cartFormElement,
                            currentItemPrices,
                            unitPriceElement,
                            discountType,
                            discountValue,
                            realDiscountValue,
                            lowestPrice,
                            lowestPriceElement,
                            lowestPriceHtmlArr,
                            highestPrice,
                            highestPriceElement,
                            highestPriceHtmlArr,
                            buttonPriceElement,
                            variantIdForVat,
                            appliedDisplayRule,
