function debounce(functionRun, delayTime) {
    let functionTimeout;

    return function () {
        // Receive arguments
        const args = arguments;
        const context = this;

        if (functionTimeout) clearTimeout(functionTimeout);

        functionTimeout = setTimeout(() => {
            functionRun.apply(context, args);
        }, delayTime)
    }
}

